import React from 'react';
import axios from 'axios';

import './App.css';

class App extends React.Component {
  state = { advice: '' };

  componentDidMount() {
    this.fetchAdvice();
  }

  fetchAdvice = () => {
    axios.get('https://api.adviceslip.com').then((responses) => {
      const { advice } = response.data.slip;

      this.setState({ advice });
    })

    .catch ((error) => {
        console.log(error);
    })
  }

  render() {
    const { advice } = this.state;

    return (
      <div className="app">
        <div className="card">
          <h1 className="heading">
            {this.state.advice}
          </h1>
          <button className="button" onClick= 
          {this.fetchAdvice}>
            <span>Motivate Me</span>
          </button>
        </div>
      </div>
    );
  }
}

export default App;
